mapper.select:
SELECT IFNULL(MAX(BOARD_SEQ) + 1, 1) 
  FROM TB_BOARD_DTL 
 WHERE BOARD_ID = #{boardId}

mapper.insert:
INSERT INTO TB_BOARD_DTL
(
        BOARD_ID
    ,   BOARD_SEQ
    ,   BOARD_TITLE
    ,   BOARD_CONTENT
    ,   CREATE_USER_ID
    ,   CREATE_DATETIME
) VALUES (
        #{boardId}
    ,   #{boardSeq}
    ,   #{boardTitle}
    ,   #{boardContent}
    ,   #{createUserId}
    ,   NOW()
)

mapper.update:
UPDATE TB_BOARD_DTL
   SET BOARD_TITLE = #{boardTitle}
     , BOARD_CONTENT = #{boardContent}
     , UPDATE_USER_ID = #{updateUserId}
     , UPDATE_DATETIME = NOW()
 WHERE BOARD_ID = #{boardId}
   AND BOARD_SEQ = #{boardSeq}

UPDATE TB_BOARD_DTL
   SET IS_DELETED = TRUE
 WHERE BOARD_ID = #{boardId}
   AND BOARD_SEQ = #{boardSeq}
