mapper.insert:
INSERT INTO TB_USER_MST
(
    USER_ID
  , PASSWORD
  , USER_NM
  , NICK_NM
  , CREATE_USER_ID
  , CREATE_DATETIME
)
VALUES
(
    #{userId}
  , #{password}
  , #{userNm} 
  , #{nickNm}
  , #{createUserId}
  , NOW()
)

mapper.update:
UPDATE TB_USER_MST
   SET PASSWORD        = #{password}
     , USER_NM         = #{userNm}
     , NICK_NM         = #{nickNm}
     , UPDATE_USER_ID  = #{userId}
     , UPDATE_DATETIME = NOW()
 WHERE USER_ID = #{userId}

mapper.select:
SELECT EXISTS ( 
  SELECT USER_ID 
    FROM TB_USER_MST
   WHERE USER_ID = #{userId}
)

SELECT EXISTS ( 
  SELECT USER_ID 
    FROM TB_USER_MST
   WHERE USER_ID = #{userId}
     AND PASSWORD = #{password}
)
